import { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import axios from 'axios';
import Header from '../components/Header';
import "./AdminDashboard.css";

const AdminDashboard = () => {
  const { token } = useSelector(state => state.auth);
  const [orders, setOrders] = useState([]);
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({ name: '', price: '', quantity: '', image: null, id: null });

  const fetchOrders = async () => {
    try {
      const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/admin/orders`, { headers: { Authorization: `Bearer ${token}` } });
      setOrders(res.data);
    } catch (err) { console.error(err); }
  };

  const fetchProducts = async () => {
    try {
      const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/products`, { headers: { Authorization: `Bearer ${token}` } });
      setProducts(res.data);
    } catch (err) { console.error(err); }
  };

  useEffect(() => {
    if (token) { fetchOrders(); fetchProducts(); }
  }, [token]);

  const handleAddProduct = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('name', newProduct.name);
    formData.append('price', newProduct.price);
    formData.append('quantity', newProduct.quantity);
    if (newProduct.image) formData.append('image', newProduct.image);

    try {
      if (newProduct.id) {
        await axios.put(`${process.env.REACT_APP_API_URL}/api/products/${newProduct.id}`, formData, { headers: { Authorization: `Bearer ${token}`, 'Content-Type': 'multipart/form-data' } });
        alert('Product updated!');
      } else {
        await axios.post(`${process.env.REACT_APP_API_URL}/api/products`, formData, { headers: { Authorization: `Bearer ${token}`, 'Content-Type': 'multipart/form-data' } });
        alert('Product added!');
      }
      setNewProduct({ name: '', price: '', quantity: '', image: null, id: null });
      fetchProducts();
    } catch (err) { console.error(err); alert('Failed to save product'); }
  };

  const handleDeleteProduct = async (id) => {
    if (!window.confirm('Are you sure you want to delete this product?')) return;
    try {
      await axios.delete(`${process.env.REACT_APP_API_URL}/api/products/${id}`, { headers: { Authorization: `Bearer ${token}` } });
      alert('Product deleted!');
      fetchProducts();
    } catch (err) { console.error(err); alert('Failed to delete product'); }
  };

  const handleEditProduct = (prod) => setNewProduct({ ...prod, image: null, id: prod._id });

  const handleUpdateOrder = async (id, status, expectedDelivery) => {
    try {
      const res = await axios.put(`${process.env.REACT_APP_API_URL}/api/admin/orders/${id}`, { status, expectedDelivery }, { headers: { Authorization: `Bearer ${token}` } });
      setOrders(orders.map(order => order._id === id ? res.data : order));
      alert('Order updated!');
    } catch (err) { console.error(err); alert('Failed to update order'); }
  };

  return (
    <>
      <Header />
      <div className="admin-page">
        <h1 className="admin-heading">Admin Dashboard</h1>

        {/* Add/Edit Product */}
        <div className="section">
          <h2 className="section-title">{newProduct.id ? 'Edit Product' : 'Add Product'}</h2>
          <form onSubmit={handleAddProduct} className="admin-card">
            <input type="text" placeholder="Name" value={newProduct.name} onChange={e => setNewProduct({ ...newProduct, name: e.target.value })} required />
            <input type="number" placeholder="Price" value={newProduct.price} onChange={e => setNewProduct({ ...newProduct, price: e.target.value })} required />
            <input type="number" placeholder="Quantity" value={newProduct.quantity} onChange={e => setNewProduct({ ...newProduct, quantity: e.target.value })} required />
            <input type="file" onChange={e => setNewProduct({ ...newProduct, image: e.target.files[0] })} />
            <button type="submit">{newProduct.id ? 'Update Product' : 'Add Product'}</button>
          </form>
        </div>

        {/* Products List */}
        <div className="section">
          <h2 className="section-title">Manage Products</h2>
          {products.map(prod => (
            <div key={prod._id} className="product-card">
              <p><strong>Name:</strong> {prod.name}</p>
              <p><strong>Price:</strong> ${prod.price}</p>
              <p><strong>Quantity:</strong> {prod.quantity}</p>
              <button onClick={() => handleEditProduct(prod)}>Edit</button>
              <button onClick={() => handleDeleteProduct(prod._id)}>Delete</button>
            </div>
          ))}
        </div>

        {/* Orders */}
        <div className="section">
          <h2 className="section-title">Manage Orders</h2>
          {orders.map(order => (
            <div key={order._id} className="order-card">
              <p><strong>User:</strong> {order.userId.name}</p>
              <p><strong>Status:</strong> {order.status}</p>
              <div className="order-products">
                <h4>Products:</h4>
                {order.products.map((prod, idx) => (
                  <div key={idx}>
                    <p><strong>{prod.productName}</strong></p>
                    <p>Quantity: {prod.quantity}</p>
                    <p>Price: ${prod.price}</p>
                  </div>
                ))}
              </div>
              <select value={order.status} onChange={e => handleUpdateOrder(order._id, e.target.value, order.expectedDelivery)}>
                <option value="Pending">Pending</option>
                <option value="Shipped">Shipped</option>
                <option value="Delivered">Delivered</option>
              </select>
              <input type="date" value={order.expectedDelivery ? new Date(order.expectedDelivery).toISOString().split('T')[0] : ''} onChange={e => handleUpdateOrder(order._id, order.status, e.target.value)} />
            </div>
          ))}
        </div>
      </div>
    </>
  );
};

export default AdminDashboard;
