import { configureStore } from '@reduxjs/toolkit';
import orderReducer from './orderSlice';
import productReducer from './productSlice';  // If you have this
// import authReducer from './authSlice';  // If needed for state.auth

export const store = configureStore({
  reducer: {
    orders: orderReducer,
    products: productReducer,  // Add if applicable
    // auth: authReducer,  // Add for state.auth.token
  },
});
