import { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { setOrders } from '../redux/orderSlice';
import axios from 'axios';

const OrderTracking = () => {
  const { list: orders } = useSelector(state => state.orders);
  const { token } = useSelector(state => state.auth);
  const dispatch = useDispatch();

  useEffect(() => {
    const fetchOrders = async () => {
      const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/orders`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      dispatch(setOrders(res.data));
    };
    fetchOrders();
  }, [dispatch, token]);

  // Helper function for status color
  const getStatusColor = (status) => {
    switch (status) {
      case "Delivered":
        return "text-green-600 font-bold";
      case "Shipped":
        return "text-blue-600 font-semibold";
      default:
        return "text-orange-600 font-semibold"; // Pending
    }
  };

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">Order Tracking</h2>

      {orders.length === 0 ? (
        <p>No orders yet.</p>
      ) : (
        orders.map(order => (
          <div key={order._id} className="border p-4 mb-6 rounded-lg shadow-md bg-white">
            {/* Order Header */}
            <h3 className="text-lg font-semibold">Order ID: {order._id}</h3>

            {/* Order Status */}
            <p className={`mt-1 ${getStatusColor(order.status)}`}>
              Status: {order.status}
            </p>

            {/* Delivery Date */}
            {order.expectedDelivery && (
              <p className="text-sm text-gray-600">
                Expected Delivery: {new Date(order.expectedDelivery).toLocaleDateString()}
              </p>
            )}

            {/* Product List */}
            <div className="mt-4 space-y-4">
              {order.products.map((item, index) => (
                <div 
                  key={index}
                  className="flex items-center bg-gray-50 p-3 rounded-lg shadow-sm"
                >

                  {/* Product Image */}
                  <img
                    src={item.productId.image}
                    alt={item.productId.name}
                    className="w-20 h-20 object-contain bg-white border rounded-md mr-4"
                    onError={(e) => e.target.src = "/placeholder-image.png"}
                  />

                  {/* Product Details */}
                  <div>
                    <p className="font-semibold">{item.productId.name}</p>
                    <p className="text-sm text-gray-700">Qty: {item.quantity}</p>
                    <p className="text-sm font-semibold text-gray-900">Price: â‚¹{item.price}</p>
                  </div>

                </div>
              ))}
            </div>
          </div>
        ))
      )}
    </div>
  );
};

export default OrderTracking;
