  // Or: const user = useSelector(state => state.auth?.user);

  // Now you can use user (e.g., user?.name)
  return (
    <header>
      {/* Example: {user ? <p>Welcome, {user.name}!</p> : <p>Please log in</p>} */}
    </header>
  );
};

export default Header;
