import { useLocation } from "react-router-dom";
import { useSelector } from "react-redux";
import "./Checkout.css";

const Checkout = () => {
  const location = useLocation();
  const buyNowProduct = location.state?.buyNowProduct;

  const cartItems = useSelector((state) => state.orders.cart);

  const itemsToBuy = buyNowProduct ? [buyNowProduct] : cartItems;

  return (
    <div style={{ padding: 20 }}>
      <h1>Checkout</h1>

      {itemsToBuy.map((item) => (
        <div key={item._id} style={{ marginBottom: 20 }}>
          <img
            src={item.image}
            alt={item.name}
            style={{ width: 100, height: 100 }}
          />
          <h3>{item.name}</h3>
          <p>Price: â‚¹{item.price}</p>
          <p>Quantity: 1</p>
        </div>
      ))}

      <button
        style={{
          padding: "10px 20px",
          background: "green",
          color: "white",
          border: "none",
          borderRadius: 6,
          cursor: "pointer",
        }}
        onClick={() => alert("Order Placed!")}
      >
        Place Order
      </button>
    </div>
  );
};

export default Checkout;
