import { useState } from "react";
import { useDispatch } from "react-redux";
import { login } from "../redux/authSlice";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import Header from "../components/Header";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post(
        `${process.env.REACT_APP_API_URL}/api/auth/login`,
        { email, password }
      );

      if (res.data.user && res.data.token) {
        dispatch(login({ user: res.data.user, token: res.data.token }));
        navigate(res.data.user.role === "admin" ? "/admin" : "/");
      } else {
        alert("Login succeeded, but user data is incomplete. Check API response.");
      }
    } catch (err) {
      alert(
        `Login failed: ${
          err.response?.data?.message || "Invalid credentials or server error"
        }`
      );
    }
  };

  return (
    <>
      <Header />

      <div className="min-h-screen flex justify-center items-center bg-gradient-to-br from-gray-100 to-gray-300 p-4">
        <div className="backdrop-blur-xl bg-white/40 border border-white/50 shadow-2xl rounded-3xl p-8 w-full max-w-md
                        transform transition-all duration-300 hover:scale-[1.02]">

          <h2 className="text-3xl font-extrabold text-gray-800 text-center mb-6 tracking-wide">
            Welcome Back ðŸ‘‹
          </h2>

          <form onSubmit={handleSubmit}>

            <div className="mb-5">
              <label className="block mb-1 font-semibold text-gray-700">Email</label>
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                className="w-full p-3 rounded-xl border border-gray-300 focus:outline-none focus:ring-2 
                           focus:ring-blue-500 shadow-sm"
                placeholder="Enter your email"
                required
              />
            </div>

            <div className="mb-6">
              <label className="block mb-1 font-semibold text-gray-700">Password</label>
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="w-full p-3 rounded-xl border border-gray-300 focus:outline-none focus:ring-2 
                           focus:ring-blue-500 shadow-sm"
                placeholder="Enter your password"
                required
              />
            </div>

            <button
              type="submit"
              className="w-full bg-blue-600 text-white py-3 rounded-xl font-semibold shadow-lg 
                         hover:bg-blue-700 transition-all duration-200"
            >
              Login
            </button>

            <p className="mt-5 text-center text-gray-700">
              Don't have an account?{" "}
              <a href="/register" className="text-blue-600 font-semibold hover:underline">
                Register
              </a>
            </p>
          </form>
        </div>
      </div>
    </>
  );
};

export default Login;
