import { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { setOrders } from '../redux/orderSlice';
import axios from 'axios';

const OrderTracking = () => {
  const { list: orders } = useSelector(state => state.orders);
  const { token } = useSelector(state => state.auth);
  const dispatch = useDispatch();

  useEffect(() => {
    const fetchOrders = async () => {
      const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/orders`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      dispatch(setOrders(res.data));
    };
    fetchOrders();
  }, [dispatch, token]);

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">Order Tracking</h2>
      {orders.length === 0 ? (
        <p>No orders yet.</p>
      ) : (
        orders.map(order => (
          <div key={order._id} className="border p-4 mb-4 rounded shadow-md">
            <h3>Order ID: {order._id}</h3>
            <p>Status: {order.status}</p>
            {order.expectedDelivery && <p>Expected Delivery: {new Date(order.expectedDelivery).toLocaleDateString()}</p>}
            <ul>
              {order.products.map(item => (
                <li key={item.productId._id}>
                  {item.productId.name} - Qty: {item.quantity} - Price: ${item.price}
                </li>
              ))}
            </ul>
          </div>
        ))
      )}
    </div>
  );
};

export default OrderTracking;
