import { configureStore } from '@reduxjs/toolkit';
import orderReducer from './orderSlice';  // Import your slice
// Import other slices if you have them (e.g., authSlice for state.auth)

export const store = configureStore({
  reducer: {
    orders: orderReducer,  // Matches state.orders in your selectors
    // Add other reducers, e.g., auth: authReducer
  },
});
