import { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { clearCart } from '../redux/orderSlice';
import axios from 'axios';
import Header from '../components/Header';
import "./CartPage.css";

const CartPage = () => {
  const cart = useSelector(state => state.orders.cart);
  const { token } = useSelector(state => state.auth);
  const dispatch = useDispatch();
  const [isLoading, setIsLoading] = useState(false);

  const handleBuyNow = async () => {
    if (cart.length === 0) return;
    setIsLoading(true);

    try {
      const products = cart.map(item => ({
        productId: item._id,
        quantity: 1,
        price: item.price
      }));

      await axios.post(`${process.env.REACT_APP_API_URL}/api/orders`, { products }, {
        headers: { Authorization: `Bearer ${token}` },
      });

      dispatch(clearCart());
      alert("Order placed! Check your dashboard for tracking.");

    } catch (error) {
      console.error('Error placing order:', error);
      alert("Failed to place order.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <>
      <Header />

      <div className="cart-page">

        <h1 className="cart-title">Your Cart</h1>

        {cart.length === 0 ? (
          <p className="empty-cart">Your cart is empty.</p>
        ) : (
          <>
            <div className="cart-grid">
              {cart.map(item => (
                <div key={item._id} className="cart-card">
                  <img
                    src={`${process.env.REACT_APP_API_URL}/${item.image}`}
                    alt={item.name}
                    className="cart-img"
                    onError={(e) => { e.target.src = '/placeholder-image.png'; }}
                  />

                  <h3 className="cart-item-name">{item.name}</h3>
                  <p className="cart-item-price">â‚¹{item.price}</p>
                </div>
              ))}
            </div>

            <button
              onClick={handleBuyNow}
              disabled={isLoading}
              className={`buy-btn ${isLoading ? "disabled-btn" : ""}`}
            >
              {isLoading ? "Placing Order..." : "Buy Now"}
            </button>
          </>
        )}
      </div>
    </>
  );
};

export default CartPage;
