import { useState } from 'react';
import { useDispatch } from 'react-redux';
import { login } from '../redux/authSlice';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import Header from '../components/Header';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post(`${process.env.REACT_APP_API_URL}/api/auth/login`, { email, password });
      console.log('Login API Response:', res.data);  // Debug: Check the response structure
      
      // Assuming res.data is { user: {...}, token: '...' }
      // If your API returns { token, user }, swap them here
      if (res.data.user && res.data.token) {
        dispatch(login({ user: res.data.user, token: res.data.token }));
        // Navigate based on role
        navigate(res.data.user.role === 'admin' ? '/admin' : '/');
      } else {
        alert('Login succeeded, but user data is incomplete. Check API response.');
      }
    } catch (err) {
      console.error('Login Error:', err.response?.data || err.message);  // Debug: Log the error
      alert(`Login failed: ${err.response?.data?.message || 'Invalid credentials or server error'}`);
    }
  };

  return (
    <>
      <Header />
      <div className="flex justify-center items-center h-screen">
        <form onSubmit={handleSubmit} className="bg-white p-6 rounded shadow-md w-80">
          <h2 className="text-xl font-bold mb-4">Login</h2>
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="w-full p-2 mb-4 border rounded"
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="w-full p-2 mb-4 border rounded"
            required
          />
          <button type="submit" className="w-full bg-blue-500 text-white p-2 rounded">Login</button>
          <p className="mt-4 text-center">
            Don't have an account? <a href="/register" className="text-blue-500">Register</a>
          </p>
        </form>
      </div>
    </>
  );
};

export default Login;
