import { useState } from 'react';  // Added for loading state
import { useSelector, useDispatch } from 'react-redux';
import { clearCart } from '../redux/orderSlice';
import axios from 'axios';
import Header from '../components/Header';

const CartPage = () => {
  const cart = useSelector(state => state.orders.cart);
  const { token } = useSelector(state => state.auth);
  const dispatch = useDispatch();
  const [isLoading, setIsLoading] = useState(false);  // For button state

  const handleBuyNow = async () => {
    if (cart.length === 0) return;  // Prevent empty orders
    setIsLoading(true);
    try {
      const products = cart.map(item => ({ productId: item._id, quantity: 1, price: item.price }));
      await axios.post(`${process.env.REACT_APP_API_URL}/api/orders`, { products }, {
        headers: { Authorization: `Bearer ${token}` },
      });
      dispatch(clearCart());  // Clear cart only on success
      alert('Order placed! Check your dashboard for tracking.');
    } catch (error) {
      console.error('Error placing order:', error);
      alert('Failed to place order. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <>
      <Header />
      <div className="p-4">
        <h1 className="text-2xl font-bold mb-4">Cart</h1>
        {cart.length === 0 ? (
          <p>Your cart is empty.</p>
        ) : (
          <>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
              {cart.map(item => (
                <div key={item._id} className="border p-4 rounded shadow-md">
                  <img 
                    src={`${process.env.REACT_APP_API_URL}/${item.image}`} 
                    alt={item.name} 
                    className="w-full h-32 object-cover mb-2" 
                    onError={(e) => { e.target.src = '/placeholder-image.png'; }}  // Fallback for missing images
                  />
                  <h3 className="font-semibold">{item.name}</h3>
                  <p>Price: ${item.price}</p>
                </div>
              ))}
            </div>
            <button 
              onClick={handleBuyNow} 
              disabled={isLoading} 
              className={`px-4 py-2 rounded ${isLoading ? 'bg-gray-400 cursor-not-allowed' : 'bg-green-500 hover:bg-green-600'} text-white`}
            >
              {isLoading ? 'Placing Order...' : 'Buy Now'}
            </button>
          </>
        )}
      </div>
    </>
  );
};

export default CartPage;
