import { useSelector, useDispatch } from 'react-redux';
import { clearCart } from '../redux/orderSlice';
import axios from 'axios';
import Header from '../components/Header';

const CartPage = () => {
  const cart = useSelector(state => state.orders.cart);
  const { token } = useSelector(state => state.auth);
  const dispatch = useDispatch();

  const handleBuyNow = async () => {
    const products = cart.map(item => ({ productId: item._id, quantity: 1, price: item.price }));
    await axios.post(`${process.env.REACT_APP_API_URL}/api/orders`, { products }, {
      headers: { Authorization: `Bearer ${token}` },
    });
    dispatch(clearCart());
    alert('Order placed! Check your dashboard for tracking.');
  };

  return (
    <>
      <Header />
      <div className="p-4">
        <h1 className="text-2xl font-bold mb-4">Cart</h1>
        {cart.length === 0 ? (
          <p>Your cart is empty.</p>
        ) : (
          <>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
              {cart.map(item => (
                <div key={item._id} className="border p-4 rounded shadow-md">
                  <img src={`${process.env.REACT_APP_API_URL}/${item.image}`} alt={item.name} className="w-full h-32 object-cover" />
                  <h3>{item.name}</h3>
                  <p>Price: ${item.price}</p>
                </div>
              ))}
            </div>
            <button onClick={handleBuyNow} className="bg-green-500 text-white px-4 py-2 rounded">Buy Now</button>
          </>
        )}
      </div>
    </>
  );
};

export default CartPage;
