import { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import axios from 'axios';
import Header from '../components/Header';
import "./AdminDashboard.css";

const AdminDashboard = () => {
  const { token } = useSelector((state) => state.auth);

  // Navigation Dropdown State
  const [activeTab, setActiveTab] = useState("addProduct");

  // Orders & Products State
  const [orders, setOrders] = useState([]);
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({
    name: '',
    price: '',
    quantity: '',
    image: null,
    id: null
  });

  // Fetch All Orders
  const fetchOrders = async () => {
    try {
      const res = await axios.get(
        `${process.env.REACT_APP_API_URL}/api/orders/admin/all`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setOrders(res.data);
    } catch (err) {
      console.error('Failed to fetch orders:', err);
      setOrders([]);
    }
  };

  // Fetch Products
  const fetchProducts = async () => {
    try {
      const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/products`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setProducts(res.data);
    } catch (err) {
      console.error('Failed to fetch products:', err);
      setProducts([]);
    }
  };

  useEffect(() => {
    if (token) {
      fetchOrders();
      fetchProducts();
    }
  }, [token]);

  // Add / Update Product
  const handleAddProduct = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('name', newProduct.name);
    formData.append('price', newProduct.price);
    formData.append('quantity', newProduct.quantity);
    if (newProduct.image) formData.append('image', newProduct.image);

    try {
      if (newProduct.id) {
        await axios.put(
          `${process.env.REACT_APP_API_URL}/api/products/${newProduct.id}`,
          formData,
          {
            headers: {
              Authorization: `Bearer ${token}`,
              'Content-Type': 'multipart/form-data'
            },
          }
        );
        alert('Product updated!');
      } else {
        await axios.post(
          `${process.env.REACT_APP_API_URL}/api/products`,
          formData,
          {
            headers: {
              Authorization: `Bearer ${token}`,
              'Content-Type': 'multipart/form-data'
            },
          }
        );
        alert('Product added!');
      }

      setNewProduct({ name: '', price: '', quantity: '', image: null, id: null });
      fetchProducts();
    } catch (err) {
      console.error(err);
      alert('Failed to save product');
    }
  };

  // Delete Product
  const handleDeleteProduct = async (id) => {
    if (!window.confirm('Are you sure you want to delete this product?')) return;
    try {
      await axios.delete(`${process.env.REACT_APP_API_URL}/api/products/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert('Product deleted!');
      fetchProducts();
    } catch (err) {
      console.error(err);
      alert('Failed to delete product');
    }
  };

  // Edit Product
  const handleEditProduct = (prod) => {
    setNewProduct({
      name: prod.name,
      price: prod.price,
      quantity: prod.quantity,
      image: null,
      id: prod._id,
    });

    setActiveTab("addProduct"); // Switch to form automatically
  };

  // Update Order
  const handleUpdateOrder = async (id, status, expectedDelivery) => {
    try {
      const res = await axios.put(
        `${process.env.REACT_APP_API_URL}/api/orders/admin/update/${id}`,
        { status, expectedDelivery },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setOrders(
        orders.map((order) =>
          order._id === id ? res.data : order
        )
      );

      alert('Order updated!');
    } catch (err) {
      console.error(err);
      alert('Failed to update order');
    }
  };

  return (
    <>
      <Header />

      <div className="admin-page">

        {/* HEADER + DROPDOWN */}
        <div className="admin-header">
          <h1 className="admin-heading">Admin Dashboard</h1>

          <select
            className="admin-dropdown"
            value={activeTab}
            onChange={(e) => setActiveTab(e.target.value)}
          >
            <option value="addProduct">Add Product</option>
            <option value="manageProducts">Manage Products</option>
            <option value="manageOrders">Manage Orders</option>
          </select>
        </div>

        {/* ADD PRODUCT */}
        {activeTab === "addProduct" && (
          <div className="section">
            <h2 className="section-title">{newProduct.id ? 'Edit Product' : 'Add Product'}</h2>

            <form onSubmit={handleAddProduct} className="admin-card">
              <input
                type="text"
                placeholder="Name"
                value={newProduct.name}
                onChange={(e) => setNewProduct({ ...newProduct, name: e.target.value })}
                className="input-field"
                required
              />

              <input
                type="number"
                placeholder="Price"
                value={newProduct.price}
                onChange={(e) => setNewProduct({ ...newProduct, price: e.target.value })}
                className="input-field"
                required
              />

              <input
                type="number"
                placeholder="Quantity"
                value={newProduct.quantity}
                onChange={(e) =>
                  setNewProduct({ ...newProduct, quantity: e.target.value })
                }
                className="input-field"
                required
              />

              {/* Show current image when editing */}
              {newProduct.id &&
                products.find((p) => p._id === newProduct.id)?.image && (
                  <div style={{ marginBottom: 10 }}>
                    <p>Current Image:</p>
                    <img
                      src={products.find((p) => p._id === newProduct.id).image}
                      alt="Current"
                      style={{
                        width: 80,
                        height: 80,
                        objectFit: 'cover',
                        borderRadius: 6,
                      }}
                    />
                  </div>
                )}

              <input
                type="file"
                onChange={(e) => setNewProduct({ ...newProduct, image: e.target.files[0] })}
                className="input-file"
              />

              <button type="submit" className="btn-yellow">
                {newProduct.id ? 'Update Product' : 'Add Product'}
              </button>
            </form>
          </div>
        )}

        {/* MANAGE PRODUCTS */}
        {activeTab === "manageProducts" && (
          <div className="section">
            <h2 className="section-title">Manage Products</h2>

            <div className="product-list">
              {products.map((prod) => (
                <div key={prod._id} className="product-card">
                  {prod.image && (
                    <img
                      src={prod.image}
                      alt={prod.name}
                      style={{
                        width: 80,
                        height: 80,
                        objectFit: 'cover',
                        borderRadius: 6,
                        marginBottom: 6,
                      }}
                    />
                  )}

                  <p><strong>Name:</strong> {prod.name}</p>
                  <p><strong>Price:</strong> ${prod.price}</p>
                  <p><strong>Quantity:</strong> {prod.quantity}</p>

                  <button className="btn-yellow" onClick={() => handleEditProduct(prod)}>
                    Edit
                  </button>

                  <button className="btn-red" onClick={() => handleDeleteProduct(prod._id)}>
                    Delete
                  </button>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* MANAGE ORDERS */}
        {activeTab === "manageOrders" && (
          <div className="section">
            <h2 className="section-title">Manage Orders</h2>

            {orders.map((order) => (
              <div key={order._id} className="order-card">
                <p><strong>User:</strong> {order.userId?.name}</p>
                <p><strong>Status:</strong> {order.status}</p>

                <div className="order-products">
                  <h4>Products:</h4>

                  {order.products.map((prod, idx) => (
                    <div key={idx} className="order-product-item">
                      <p><strong>{prod.productName}</strong></p>
                      <p>Qty: {prod.quantity}</p>
                      <p>Price: â‚¹{prod.price}</p>
                    </div>
                  ))}
                </div>

                <select
                  value={order.status}
                  onChange={(e) =>
                    handleUpdateOrder(order._id, e.target.value, order.expectedDelivery)
                  }
                  className="select-field"
                >
                  <option value="Pending">Pending</option>
                  <option value="Shipped">Shipped</option>
                  <option value="Delivered">Delivered</option>
                </select>

                <input
                  type="date"
                  value={
                    order.expectedDelivery
                      ? new Date(order.expectedDelivery).toISOString().split('T')[0]
                      : ''
                  }
                  onChange={(e) =>
                    handleUpdateOrder(order._id, order.status, e.target.value)
                  }
                  className="date-field"
                />
              </div>
            ))}
          </div>
        )}
      </div>
    </>
  );
};

export default AdminDashboard;
