import { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import axios from 'axios';
import Header from '../components/Header';
import "./AdminDashboard.css";

const AdminDashboard = () => {
  const { token } = useSelector(state => state.auth);
  const [orders, setOrders] = useState([]);
  const [newProduct, setNewProduct] = useState({ name: '', price: '', quantity: '', image: null });

  useEffect(() => {
    if (!token) {
      setOrders([]);
      return;
    }
    const fetchOrders = async () => {
      try {
        const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/admin/orders`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setOrders(res.data);
      } catch (error) {
        console.error('Failed to fetch orders:', error);
        setOrders([]);
      }
    };
    fetchOrders();
  }, [token]);

  const handleAddProduct = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('name', newProduct.name);
    formData.append('price', newProduct.price);
    formData.append('quantity', newProduct.quantity);
    formData.append('image', newProduct.image);
    await axios.post(`${process.env.REACT_APP_API_URL}/api/products`, formData, {
      headers: { Authorization: `Bearer ${token}`, 'Content-Type': 'multipart/form-data' },
    });
    alert('Product added!');
    setNewProduct({ name: '', price: '', quantity: '', image: null });
  };

  const handleUpdateOrder = async (id, status, expectedDelivery) => {
    try {
      const res = await axios.put(`${process.env.REACT_APP_API_URL}/api/admin/orders/${id}`, { status, expectedDelivery }, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setOrders(orders.map(order => order._id === id ? res.data : order));
      alert('Order updated!');
    } catch (error) {
      console.error('Failed to update order:', error);
      alert('Failed to update order');
    }
  };

  return (
    <>
      <Header />
      <div className="admin-page">

        <h1 className="admin-heading">Admin Dashboard</h1>

        {/* Add Product */}
        <div className="section">
          <h2 className="section-title">Add Product</h2>

          <form onSubmit={handleAddProduct} className="admin-card">

            <input
              type="text"
              placeholder="Name"
              value={newProduct.name}
              onChange={(e) => setNewProduct({ ...newProduct, name: e.target.value })}
              className="input-field"
              required
            />

            <input
              type="number"
              placeholder="Price"
              value={newProduct.price}
              onChange={(e) => setNewProduct({ ...newProduct, price: e.target.value })}
              className="input-field"
              required
            />

            <input
              type="number"
              placeholder="Quantity"
              value={newProduct.quantity}
              onChange={(e) => setNewProduct({ ...newProduct, quantity: e.target.value })}
              className="input-field"
              required
            />

            <input
              type="file"
              onChange={(e) => setNewProduct({ ...newProduct, image: e.target.files[0] })}
              className="input-file"
              required
            />

            <button type="submit" className="btn-yellow">Add Product</button>
          </form>
        </div>

        {/* Orders */}
        <div className="section">
          <h2 className="section-title">Manage Orders</h2>

          {orders.map(order => (
            <div key={order._id} className="order-card">
              <p><strong>User:</strong> {order.userId.name}</p>
              <p><strong>Status:</strong> {order.status}</p>

              {/* Product Details */}
              <div className="order-products">
                <h4>Products:</h4>
                {order.products.map((prod, idx) => (
                  <div key={idx} className="order-product-item" style={{ display: 'flex', alignItems: 'center', marginBottom: 8 }}>
                    <img
                      src={`${process.env.REACT_APP_API_URL}/uploads/${prod.productImage}`}
                      alt={prod.productName}
                      style={{ width: 60, height: 60, objectFit: 'cover', marginRight: 10, borderRadius: 4 }}
                    />
                    <div>
                      <p style={{ margin: 0, fontWeight: 'bold' }}>{prod.productName}</p>
                      <p style={{ margin: 0 }}>Quantity: {prod.quantity}</p>
                      <p style={{ margin: 0 }}>Price: ${prod.price}</p>
                    </div>
                  </div>
                ))}
              </div>

              {/* Editable Status */}
              <select
                value={order.status}
                onChange={(e) => handleUpdateOrder(order._id, e.target.value, order.expectedDelivery)}
                className="select-field"
              >
                <option value="Pending">Pending</option>
                <option value="Shipped">Shipped</option>
                <option value="Delivered">Delivered</option>
              </select>

              {/* Editable Expected Delivery Date */}
              <input
                type="date"
                value={order.expectedDelivery ? new Date(order.expectedDelivery).toISOString().split('T')[0] : ''}
                onChange={(e) => handleUpdateOrder(order._id, order.status, e.target.value)}
                className="date-field"
              />
            </div>
          ))}
        </div>
      </div>
    </>
  );
};

export default AdminDashboard;
